import React, { useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";
import {
  Activity,
  TrendingUp,
  Database,
  FileText,
  AlertTriangle,
  Stethoscope,
  Sun,
  Moon,
} from "lucide-react";

const CardioPredictAI = () => {
  const [activeTab, setActiveTab] = useState("clinical");
  const [predictionResult, setPredictionResult] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [patientData, setPatientData] = useState({
    age: 54,
    sex: 1,
    cp: 0,
    trestbps: 130,
    chol: 240,
    fbs: 0,
    restecg: 0,
    thalach: 150,
    exang: 0,
    oldpeak: 1.0,
    slope: 1,
    ca: 0,
    thal: 2,
  });

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  const featureImportance = [
    { feature: "Chest Pain", importance: 23 },
    { feature: "Max Heart Rate", importance: 19 },
    { feature: "ST Depression", importance: 16 },
    { feature: "Age", importance: 14 },
    { feature: "Cholesterol", importance: 12 },
  ];

  const modelComparison = [
    { model: "Logistic Regression", accuracy: 85, precision: 83, recall: 87 },
    { model: "Random Forest", accuracy: 89, precision: 88, recall: 91 },
    { model: "SVM", accuracy: 82, precision: 80, recall: 85 },
    { model: "Gradient Boosting", accuracy: 91, precision: 90, recall: 93 },
  ];

  const calculateRiskScore = (data) => {
    let score = 0;
    if (data.age > 60) score += 0.15;
    else if (data.age > 50) score += 0.1;
    else if (data.age > 40) score += 0.05;
    if (data.sex === 1) score += 0.08;
    if (data.cp === 0) score += 0.2;
    else if (data.cp === 1) score += 0.15;
    else if (data.cp === 2) score += 0.1;
    if (data.trestbps > 140) score += 0.08;
    if (data.chol > 240) score += 0.1;
    if (data.fbs === 1) score += 0.03;
    if (data.exang === 1) score += 0.08;
    if (data.oldpeak > 1.0) score += 0.08;
    score += data.ca * 0.06;
    if (data.thal === 3) score += 0.1;
    return Math.min(score, 0.95);
  };

  const runPrediction = () => {
    const riskScore = calculateRiskScore(patientData);
    const confidence = 0.75 + Math.random() * 0.2;
    const factors = [];

    if (patientData.age > 60) factors.push("Advanced age");
    if (patientData.sex === 1) factors.push("Male gender");
    if (patientData.cp === 0) factors.push("Typical angina");
    if (patientData.trestbps > 140) factors.push("High blood pressure");
    if (patientData.chol > 240) factors.push("High cholesterol");
    if (patientData.exang === 1) factors.push("Exercise-induced angina");
    if (patientData.ca > 0) factors.push("Major vessel blockage");

    const recommendations = [];
    if (riskScore > 0.7) {
      recommendations.push("Immediate cardiology consultation");
      recommendations.push("Consider stress testing");
    } else if (riskScore > 0.5) {
      recommendations.push("Cardiology evaluation within 2-4 weeks");
    } else {
      recommendations.push("Routine preventive care");
      recommendations.push("Lifestyle modification");
    }

    setPredictionResult({
      probability: riskScore,
      prediction: riskScore > 0.5 ? "High Risk" : "Low Risk",
      confidence: confidence,
      riskFactors: factors,
      recommendations: recommendations,
    });
  };

  const loadRandomCase = () => {
    const cases = [
      {
        age: 63,
        sex: 1,
        cp: 0,
        trestbps: 145,
        chol: 233,
        fbs: 1,
        restecg: 0,
        thalach: 150,
        exang: 0,
        oldpeak: 2.3,
        slope: 0,
        ca: 0,
        thal: 1,
      },
      {
        age: 29,
        sex: 1,
        cp: 1,
        trestbps: 130,
        chol: 204,
        fbs: 0,
        restecg: 0,
        thalach: 202,
        exang: 0,
        oldpeak: 0,
        slope: 2,
        ca: 0,
        thal: 2,
      },
      {
        age: 57,
        sex: 0,
        cp: 2,
        trestbps: 120,
        chol: 354,
        fbs: 0,
        restecg: 1,
        thalach: 163,
        exang: 1,
        oldpeak: 0.6,
        slope: 2,
        ca: 0,
        thal: 2,
      },
    ];
    const randomCase = cases[Math.floor(Math.random() * cases.length)];
    setPatientData(randomCase);
  };

  const resetForm = () => {
    setPatientData({
      age: 54,
      sex: 1,
      cp: 0,
      trestbps: 130,
      chol: 240,
      fbs: 0,
      restecg: 0,
      thalach: 150,
      exang: 0,
      oldpeak: 1.0,
      slope: 1,
      ca: 0,
      thal: 2,
    });
    setPredictionResult(null);
  };

  const TabButton = ({ id, label, icon: Icon }) => (
    <button
      onClick={() => setActiveTab(id)}
      className={`flex items-center px-4 py-2 rounded-lg font-medium transition-colors ${
        activeTab === id
          ? isDarkMode
            ? "bg-blue-600 text-white"
            : "bg-blue-500 text-white"
          : isDarkMode
          ? "bg-gray-700 text-gray-300 hover:bg-gray-600"
          : "bg-gray-100 text-gray-700 hover:bg-gray-200"
      }`}
    >
      <Icon className="w-4 h-4 mr-2" />
      {label}
    </button>
  );

  return (
    <div
      className={`min-h-screen ${isDarkMode ? "bg-gray-900" : "bg-gray-50"}`}
    >
      <div className="max-w-7xl mx-auto p-6">
        <div className="fixed top-4 right-4 z-50">
          <button
            onClick={toggleTheme}
            className={`p-3 rounded-full shadow-lg transition-all duration-300 hover:scale-110 ${
              isDarkMode
                ? "bg-yellow-500 text-gray-900"
                : "bg-gray-800 text-yellow-400"
            }`}
          >
            {isDarkMode ? (
              <Sun className="w-5 h-5" />
            ) : (
              <Moon className="w-5 h-5" />
            )}
          </button>
        </div>

        <div className="text-center mb-8">
          <h1
            className={`text-4xl font-bold mb-2 ${
              isDarkMode ? "text-blue-400" : "text-blue-600"
            }`}
          >
            CardioPredict AI
          </h1>
          <p
            className={`text-xl mb-2 ${
              isDarkMode ? "text-gray-300" : "text-gray-600"
            }`}
          >
            Intelligent Cardiovascular Risk Assessment Using Machine Learning
          </p>
          <p
            className={`text-lg mb-1 ${
              isDarkMode ? "text-gray-400" : "text-gray-500"
            }`}
          >
            Advanced Clinical Decision Support System
          </p>
          <p
            className={`text-md italic ${
              isDarkMode ? "text-gray-500" : "text-gray-400"
            }`}
          >
            Developed for Educational & Research Purposes
          </p>
        </div>

        <div className="flex flex-wrap gap-2 mb-8 justify-center">
          <TabButton
            id="clinical"
            label="Clinical Prediction Tool"
            icon={Stethoscope}
          />
          <TabButton
            id="performance"
            label="Model Performance"
            icon={TrendingUp}
          />
          <TabButton
            id="methodology"
            label="Research Methodology"
            icon={Database}
          />
          <TabButton
            id="validation"
            label="Literature Validation"
            icon={FileText}
          />
        </div>

        {activeTab === "clinical" && (
          <div className="space-y-6">
            <div
              className={`border rounded-lg p-4 ${
                isDarkMode
                  ? "bg-yellow-900 border-yellow-600"
                  : "bg-yellow-100 border-yellow-400"
              }`}
            >
              <div className="flex items-start">
                <AlertTriangle
                  className={`w-5 h-5 mr-2 mt-0.5 ${
                    isDarkMode ? "text-yellow-400" : "text-yellow-600"
                  }`}
                />
                <div>
                  <h3
                    className={`font-semibold ${
                      isDarkMode ? "text-yellow-200" : "text-yellow-800"
                    }`}
                  >
                    Disclaimer:
                  </h3>
                  <p
                    className={
                      isDarkMode ? "text-yellow-300" : "text-yellow-800"
                    }
                  >
                    This tool is for educational and research purposes only.
                    Always consult qualified healthcare professionals.
                  </p>
                </div>
              </div>
            </div>

            <div
              className={`border rounded-lg shadow-sm p-6 ${
                isDarkMode
                  ? "bg-gray-800 border-gray-700"
                  : "bg-white border-gray-200"
              }`}
            >
              <h2
                className={`text-2xl font-bold mb-4 ${
                  isDarkMode ? "text-white" : "text-gray-900"
                }`}
              >
                Patient Clinical Data Entry
              </h2>

              <div className="grid md:grid-cols-3 gap-6 mb-6">
                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    Age (years)
                  </label>
                  <input
                    type="number"
                    value={patientData.age}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        age: parseInt(e.target.value) || 0,
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  />
                </div>

                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    Biological Sex
                  </label>
                  <select
                    value={patientData.sex}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        sex: parseInt(e.target.value),
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  >
                    <option value={1}>Male</option>
                    <option value={0}>Female</option>
                  </select>
                </div>

                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    Chest Pain Type
                  </label>
                  <select
                    value={patientData.cp}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        cp: parseInt(e.target.value),
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  >
                    <option value={0}>Typical Angina</option>
                    <option value={1}>Atypical Angina</option>
                    <option value={2}>Non-Anginal Pain</option>
                    <option value={3}>Asymptomatic</option>
                  </select>
                </div>

                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    Resting BP (mmHg)
                  </label>
                  <input
                    type="number"
                    value={patientData.trestbps}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        trestbps: parseInt(e.target.value) || 0,
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  />
                </div>

                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    Cholesterol (mg/dl)
                  </label>
                  <input
                    type="number"
                    value={patientData.chol}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        chol: parseInt(e.target.value) || 0,
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  />
                </div>

                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    Fasting Blood Sugar
                  </label>
                  <select
                    value={patientData.fbs}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        fbs: parseInt(e.target.value),
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  >
                    <option value={0}> 120 mg/dl</option>
                    <option value={1}>> 120 mg/dl</option>
                  </select>
                </div>

                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    Max Heart Rate
                  </label>
                  <input
                    type="number"
                    value={patientData.thalach}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        thalach: parseInt(e.target.value) || 0,
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  />
                </div>

                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    Exercise Angina
                  </label>
                  <select
                    value={patientData.exang}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        exang: parseInt(e.target.value),
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  >
                    <option value={0}>No</option>
                    <option value={1}>Yes</option>
                  </select>
                </div>

                <div>
                  <label
                    className={`block text-sm font-semibold mb-2 ${
                      isDarkMode ? "text-gray-300" : "text-gray-700"
                    }`}
                  >
                    ST Depression
                  </label>
                  <input
                    type="number"
                    step="0.1"
                    value={patientData.oldpeak}
                    onChange={(e) =>
                      setPatientData({
                        ...patientData,
                        oldpeak: parseFloat(e.target.value) || 0,
                      })
                    }
                    className={`w-full px-3 py-2 border rounded-md ${
                      isDarkMode
                        ? "bg-gray-700 border-gray-600 text-white"
                        : "bg-white border-gray-300 text-gray-900"
                    }`}
                  />
                </div>
              </div>

              <div className="flex gap-3 flex-wrap">
                <button
                  onClick={runPrediction}
                  className={`px-6 py-3 rounded-lg font-medium flex items-center ${
                    isDarkMode
                      ? "bg-blue-600 hover:bg-blue-700"
                      : "bg-blue-500 hover:bg-blue-600"
                  } text-white`}
                >
                  <Activity className="w-5 h-5 mr-2" />
                  Run Prediction
                </button>
                <button
                  onClick={loadRandomCase}
                  className="px-4 py-3 rounded-lg font-medium bg-gray-600 hover:bg-gray-700 text-white"
                >
                  Load Random Case
                </button>
                <button
                  onClick={resetForm}
                  className="px-4 py-3 rounded-lg font-medium bg-gray-500 hover:bg-gray-600 text-white"
                >
                  Reset
                </button>
              </div>

              {predictionResult && (
                <div
                  className={`mt-8 p-6 rounded-lg ${
                    isDarkMode ? "bg-gray-700" : "bg-gray-50"
                  }`}
                >
                  <h3
                    className={`text-xl font-semibold mb-4 ${
                      isDarkMode ? "text-white" : "text-gray-900"
                    }`}
                  >
                    Results
                  </h3>
                  <div className="grid md:grid-cols-4 gap-4 mb-6">
                    <div
                      className={`p-4 rounded-lg text-center ${
                        isDarkMode ? "bg-gray-800" : "bg-white"
                      }`}
                    >
                      <div
                        className={`text-3xl font-bold ${
                          predictionResult.prediction === "High Risk"
                            ? "text-red-600"
                            : "text-green-600"
                        }`}
                      >
                        {predictionResult.prediction}
                      </div>
                    </div>
                    <div
                      className={`p-4 rounded-lg text-center ${
                        isDarkMode ? "bg-gray-800" : "bg-white"
                      }`}
                    >
                      <div className="text-3xl font-bold text-blue-600">
                        {(predictionResult.probability * 100).toFixed(0)}%
                      </div>
                      <div
                        className={`text-sm ${
                          isDarkMode ? "text-gray-400" : "text-gray-600"
                        }`}
                      >
                        Risk
                      </div>
                    </div>
                    <div
                      className={`p-4 rounded-lg text-center ${
                        isDarkMode ? "bg-gray-800" : "bg-white"
                      }`}
                    >
                      <div className="text-3xl font-bold text-purple-600">
                        {(predictionResult.confidence * 100).toFixed(0)}%
                      </div>
                      <div
                        className={`text-sm ${
                          isDarkMode ? "text-gray-400" : "text-gray-600"
                        }`}
                      >
                        Confidence
                      </div>
                    </div>
                    <div
                      className={`p-4 rounded-lg text-center ${
                        isDarkMode ? "bg-gray-800" : "bg-white"
                      }`}
                    >
                      <div className="text-3xl font-bold text-orange-600">
                        {predictionResult.riskFactors.length}
                      </div>
                      <div
                        className={`text-sm ${
                          isDarkMode ? "text-gray-400" : "text-gray-600"
                        }`}
                      >
                        Factors
                      </div>
                    </div>
                  </div>

                  {predictionResult.riskFactors.length > 0 && (
                    <div className="mb-4">
                      <h4
                        className={`font-semibold mb-2 ${
                          isDarkMode ? "text-white" : "text-gray-900"
                        }`}
                      >
                        Risk Factors:
                      </h4>
                      <ul className="list-disc list-inside space-y-1">
                        {predictionResult.riskFactors.map((factor, i) => (
                          <li
                            key={i}
                            className={
                              isDarkMode ? "text-gray-300" : "text-gray-700"
                            }
                          >
                            {factor}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}

                  <div>
                    <h4
                      className={`font-semibold mb-2 ${
                        isDarkMode ? "text-white" : "text-gray-900"
                      }`}
                    >
                      Recommendations:
                    </h4>
                    <ul className="list-disc list-inside space-y-1">
                      {predictionResult.recommendations.map((rec, i) => (
                        <li
                          key={i}
                          className={
                            isDarkMode ? "text-gray-300" : "text-gray-700"
                          }
                        >
                          {rec}
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === "performance" && (
          <div
            className={`p-6 border rounded-lg ${
              isDarkMode
                ? "bg-gray-800 border-gray-700"
                : "bg-white border-gray-200"
            }`}
          >
            <h2
              className={`text-2xl font-bold mb-4 ${
                isDarkMode ? "text-white" : "text-gray-900"
              }`}
            >
              Model Performance
            </h2>
            <ResponsiveContainer width="100%" height={400}>
              <BarChart data={modelComparison}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="model" />
                <YAxis domain={[70, 100]} />
                <Tooltip />
                <Legend />
                <Bar dataKey="accuracy" fill="#3b82f6" name="Accuracy (%)" />
                <Bar dataKey="precision" fill="#10b981" name="Precision (%)" />
                <Bar dataKey="recall" fill="#f59e0b" name="Recall (%)" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        )}

        {activeTab === "methodology" && (
          <div
            className={`p-8 border rounded-lg ${
              isDarkMode
                ? "bg-gray-800 border-gray-700"
                : "bg-white border-gray-200"
            }`}
          >
            <h2
              className={`text-2xl font-bold mb-4 ${
                isDarkMode ? "text-white" : "text-gray-900"
              }`}
            >
              Research Methodology
            </h2>
            <p className={isDarkMode ? "text-gray-300" : "text-gray-700"}>
              This study utilizes the Cleveland Heart Disease dataset from the
              UCI Machine Learning Repository, containing 303 patient records
              with 13 clinical features and a binary target variable indicating
              the presence or absence of heart disease.
            </p>
          </div>
        )}

        {activeTab === "validation" && (
          <div
            className={`p-8 border rounded-lg ${
              isDarkMode
                ? "bg-gray-800 border-gray-700"
                : "bg-white border-gray-200"
            }`}
          >
            <h2
              className={`text-2xl font-bold mb-4 ${
                isDarkMode ? "text-white" : "text-gray-900"
              }`}
            >
              Literature Validation
            </h2>
            <p className={isDarkMode ? "text-gray-300" : "text-gray-700"}>
              The model's identification of chest pain type, age, and maximum
              heart rate as primary predictors aligns with established
              cardiovascular risk assessment guidelines from the American Heart
              Association and European Society of Cardiology.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CardioPredictAI;
